
public class 반복문_for2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(char ch='A';ch<='Z';ch++) {
			System.out.print(ch +" ");
		}
		System.out.println();
		char ch = 'A';
		while(ch<='Z') {
			System.out.print(ch + " ");
			ch++;
		}
	}

}
